"use strict";angular.module("angular-highlight-text",[]).filter("highlightText",function($sce){return function(input,match,className){var output=$sce.valueOf(input);return match&&(output=output.replace(new RegExp(match,"gi"),'<span class="'+(className||"highlighted")+'">$&</span>')),$sce.trustAsHtml(output)}}).directive("highlightText",["$sce","$filter",function($sce,$filter){return{restrict:"A",scope:{highlightText:"="},link:function(scope,element,attrs){scope.$watch("highlightText",function(options){return element.html($filter("highlightText")(options.text,options.match,attrs.highlightClass))})}}}]);